const { Client } = require\(''pg''\);
const client = new Client\({
  connectionString: process.env.DATABASE_URL || ''postgresql://postgres:postgres@localhost:5432/videoall''
}\);

async function checkData\(\) {
  await client.connect\(\);
  const result = await client.query\(\\`
    SELECT id, title, platform, 
           like_count, collect_count, comment_count, share_count, view_count, 
           publish_time, created_at
    FROM contents 
    ORDER BY created_at DESC 
    LIMIT 3
  \\`\);

  console.log\(''数据库中的最新3条记录:''\);
  console.log\(JSON.stringify\(result.rows, null, 2\)\);

  await client.end\(\);
}

checkData\(\).catch\(console.error\);
")
